//                     console.log('init')
//                     const res=await openCrawlerWeb()
//                     broadcastMessage(
//                         wss.clients,['output',[res]],{
//                             type:'success',
//                             msg: 'Message sent.',
//                         }
//                     );
//                     break;
//                 }
//             }
//         }
