import { useState ,useEffect, useContext,createContext} from "react";
let client = new WebSocket('ws://localhost:4000')
const ChatContext=createContext({
    status:{},

    books:[],
    sendMessage:()=>{},
    clearMessages:()=>{},
    displayStatus:()=>{},
});
//開啟後執行的動作，指定一個 function 會在連結 WebSocket 後執行
const ChatProvider=(props)=>{
    const [books,setBooks]=useState([]);
    const [status,setStatus]=useState({});
    client.onmessage=(byteString)=>{
        const {data}=byteString;
        const [task,payload]=JSON.parse(data);
        console.log('client task:'+task);
        switch(task){
            case 'output':{
                setBooks(()=>[...books,...payload]);
                console.log("yayaya"+payload)
                break;
            }
            default:break;
        }
    }
    const sendData = async (data) => {

        let a=await client.send( JSON.stringify(data));
        console.log("dkkkkk"+a);
    };

    const sendMessage=(payload)=>{
        sendData(["input",payload]);
        console.log(payload);
    }
    const clearMessages=()=>{
        sendData(['clear']);
    };
    return (
        <ChatContext.Provider
            value={{
                status,books,
                sendMessage,clearMessages
            }}
            {...props}
        />

    );
}
const toback=()=>useContext(ChatContext)
export{ChatProvider,toback}

