      ],
  ]  )

    useEffect(()=>{   
      if(egg){
        sendMessage("hehe");
        console.log('hoho')  
        egg=false;
      }

    })   
    useEffect(()=>{
      if(books){
      let a=[]
      for (let i=0;i<4;i++){
        let b=[]
        for(let j=0;j<3;j++){
          b.push(books[3*i+j])
        }
        a.push(b);
      }
      setImgs(a);}
    },[books])
    let imgs_post = imgs.map((i)=>
        <MDBRow>
        <MDBCol lg='4' md='12' className='mb-4'>
          <img
            src={i[0]}  
            className='img-fluid shadow-2-strong rounded-4'
            alt=''
          />
        </MDBCol>
        <MDBCol lg='4' md='6' className='mb-4'>
          <img
            src={i[1]}
            className='img-fluid shadow-2-strong rounded-4'
            alt=''
          />
        </MDBCol>
        <MDBCol lg='4' md='6' className='mb-4'>
          <img
            src={i[2]}
            className='img-fluid shadow-2-strong rounded-4'
            alt=''
          />
        </MDBCol>
        </MDBRow>
    )
    // console.log(imgs_post)
  return (
    <MDBContainer>
        {imgs_post}
    </MDBContainer>
  )
}

function Tab() {
  const [basicActive, setBasicActive] = useState('tab1');
  const device=useRWD();
  const handleBasicClick = (value: string) => {
    if (value === basicActive) {
      return;
    }

    setBasicActive(value);
  }; 
  console.log(device)
  return (
    <div className = {(device === "mobile") ? "Tab-mobile" : "Tab"}>
      <MDBTabs className='mb-3'>
        <MDBTabsItem>
          <MDBTabsLink onClick={() => handleBasicClick('tab1')} active={basicActive === 'tab1'}>
          New
          </MDBTabsLink>
        </MDBTabsItem>
        <MDBTabsItem>
          <MDBTabsLink onClick={() => handleBasicClick('tab2')} active={basicActive === 'tab2'}>
            For you
          </MDBTabsLink>
        </MDBTabsItem>
        <MDBTabsItem>
          <MDBTabsLink onClick={() => handleBasicClick('tab3')} active={basicActive === 'tab3'}>
            Following
          </MDBTabsLink>
        </MDBTabsItem>
      </MDBTabs>

      <MDBTabsContent>
        <MDBTabsPane show={basicActive === 'tab1'}><Recommendation/></MDBTabsPane>
        <MDBTabsPane show={basicActive === 'tab2'}><Recommendation/></MDBTabsPane>
        <MDBTabsPane show={basicActive === 'tab3'}><Recommendation/></MDBTabsPane>
      </MDBTabsContent>
    </div>
  );
}

