        <MDBCollapse show={showShow}>
        <br/>
        <MDBTextArea label='Post' id='textAreaExample' rows={4} />
        <br/>
        <MDBBtn size='lg' floating style={{ background: 'linear-gradient(to right, #84fab0, #8fd3f4)' }}>
        <MDBIcon far icon="paper-plane" />
